library asset_magic_eight_ball_frontend_lib_src_game_game_component.css.dart;


final List<dynamic> styles = ['  section{\n    text-align: center;\n  }\n\n  /* 8ball styles */\n  div.eight-ball {\n    background-color: black;\n    border: 1px solid black;\n    border-radius: 50%;\n    width: 300px;\n    height: 300px;\n    margin: 0 auto;\n  }\n  \n  div.answer {\n    background-color: #333;\n    border: 1px solid #333;\n    border-radius: 50%;\n    width: 50%;\n    height: 50%;\n    margin: 25% auto;\n  }\n  \n  p#eight {\n    font-size: 5em;\n    margin: 20% auto;\n    color: white;\n  }\n  \n  p#answer {\n    padding: 25px;\n    color: lightblue;\n    letter-spacing: 2px;\n  }\n\n/* Shaking animation */\n\n  .shaking {\n    /* Start the shake animation and make the animation last for 0.5 seconds */\n    animation: shake 1s; \n    /* Animation only executes one time */\n    animation-iteration-count: one; \n  }\n  /* Rotating the ball*/\n  @keyframes shake {\n    0% { transform: translate(1px, 1px) rotate(0deg); }\n    10% { transform: translate(-1px, -2px) rotate(-50deg); }\n    20% { transform: translate(-3px, 0px) rotate(50deg); }\n    30% { transform: translate(3px, 2px) rotate(0deg); }\n    40% { transform: translate(1px, -1px) rotate(50deg); }\n    50% { transform: translate(-1px, 2px) rotate(-50deg); }\n    60% { transform: translate(-3px, 1px) rotate(0deg); }\n    70% { transform: translate(3px, 1px) rotate(-50deg); }\n    80% { transform: translate(-1px, -1px) rotate(50deg); }\n    90% { transform: translate(1px, 2px) rotate(0deg); }\n    100% { transform: translate(1px, -2px) rotate(-50deg); }\n  }'];
